import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { User } from '../../models/user.model';
import { DomSanitizer } from '@angular/platform-browser';
declare var JitsiMeetExternalAPI: any;

@Component({
  selector: 'app-jitsi',
  templateUrl: './jitsi.component.html',
  styleUrls: ['./jitsi.component.scss']
})
export class JitsiComponent implements OnInit {
  api: any;
  roomName: String | undefined;
  user: User;
  options: any;
  domain: string | undefined;
  possible = "1234567890";
  lengthOfCode = 40;

  // For Custom Controls
  isAudioMuted = true;
  isVideoMuted = true;
  constructor(
    private router: Router,
    public sanitizer: DomSanitizer
  ) {
    this.domain = 'localhost:8443';
    this.roomName = 'SalaTeste';

    let name = this.makeRandom(this.lengthOfCode, this.possible);
    this.user = new User(this.roomName, "MR " + name);
  }

  async ngOnInit() {
    this.startRoom();
  }

  startRoom(): void {
    let myNode: HTMLElement = <HTMLElement>document.getElementById('jitsi-iframe');
    myNode.innerHTML = '';

    this.options = {
      roomName: this.roomName,
      width: 900,
      height: 500,
      configOverwrite: {
        prejoinPageEnabled: false,
        openBridgeChannel: 'datachannel',
        DISABLE_DOMINANT_SPEAKER_INDICATOR: true
      },
      interfaceConfigOverwrite: {
        // overwrite interface properties
      },
      parentNode: document.querySelector('#jitsi-iframe'),
      userInfo: {
        displayName: this.user.name,
      },
    };

    console.log('nameRoom', this.roomName);
    console.log('prejoinPageEnabled: ', (this.user.name != '' ? true : false));

    this.api = new JitsiMeetExternalAPI(this.domain, this.options);

    this.api.addEventListeners({
      cameraError: this.cameraErrorEvent,
      avatarChanged: this.avatarChangedEvent,
      audioAvailabilityChanged: this.audioAvailabilityChangedEvent,
      audioMuteStatusChanged: this.audioMuteStatusChangedEvent,
      breakoutRoomsUpdated: this.breakoutRoomsUpdatedEvent,
      browserSupport: this.browserSupportEvent,
      contentSharingParticipantsChanged: this.contentSharingParticipantsChangedEvent,
      dataChannelOpened: this.dataChannelOpenedEvent,
      endpointTextMessageReceived: this.endpointTextMessageReceivedEvent,
      nonParticipantMessageReceived: this.nonParticipantMessageReceivedEvent,
      faceLandmarkDetected: this.faceLandmarkDetectedEvent,
      errorOccurred: this.errorOccurredEvent,
      knockingParticipant: this.knockingParticipantEvent,
      largeVideoChanged: this.largeVideoChangedEvent,
      log: this.logEvent,

      // #########
      readyToClose: this.readyToCloseEvent,
      participantLeft: this.participantLeftEvent,
      participantJoined: this.participantJoinedEvent,
      videoConferenceJoined: this.videoConferenceJoinedEvent,
      videoConferenceLeft: this.videoConferenceLeftEvent,
      videoMuteStatusChanged: this.videoMuteStatusChangedEvent,
      participantRoleChanged: this.participantRoleChangedEvent,
      passwordRequired: this.passwordRequiredEvent,
    });
  }

  // Functions

  functions() {
    // Captures a screenshot for the participant in the large video view (on stage).
    this.api.captureLargeVideoScreenshot().then((data: any) => {
      // data is an Object with only one param, dataURL
      // data.dataURL = "data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAABQAA..."
    });

    // Retrieves a list of available devices.
    this.api.getAvailableDevices().then((devices: any) => {
      // devices = {
      //     audioInput: [{
      //         deviceId: 'ID'
      //         groupId: 'grpID'
      //         kind: 'audioinput'
      //         label: 'label'
      //     },....],
      //     audioOutput: [{
      //         deviceId: 'ID'
      //         groupId: 'grpID'
      //         kind: 'audioOutput'
      //         label: 'label'
      //     },....],
      //     videoInput: [{
      //         deviceId: 'ID'
      //         groupId: 'grpID'
      //         kind: 'videoInput'
      //         label: 'label'
      //     },....]
      // }
    });

    // Returns a promise which resolves with an array of currently sharing participants ID's.
    this.api.getContentSharingParticipants().then((res: any) => {
      //res.sharingParticipantIds = [particId1, particId2, ...]
    });

    // Retrieves a list of currently selected devices.
    this.api.getCurrentDevices().then((devices: any) => {
      // devices = {
      //     audioInput: {
      //         deviceId: 'ID'
      //         groupId: 'grpID'
      //         kind: 'videoInput'
      //         label: 'label'
      //     },
      //     audioOutput: {
      //         deviceId: 'ID'
      //         groupId: 'grpID'
      //         kind: 'videoInput'
      //         label: 'label'
      //     },
      //     videoInput: {
      //         deviceId: 'ID'
      //         groupId: 'grpID'
      //         kind: 'videoInput'
      //         label: 'label'
      //     }
      // }
    });

    // Retrieves an object containing information about the deployment.
    this.api.getDeploymentInfo().then((deploymentInfo: any) => {
      // deploymentInfo = {
      //     region: 'deployment-region',
      //     shard: 'deployment-shard',
      //     ...
      // }
    });

    // Retrieves an object containing information about livestreamUrl of the current live stream.
    this.api.getLivestreamUrl().then((livestreamData: any) => {
      // livestreamData = {
      //     livestreamUrl: 'livestreamUrl'
      // }
    });

    // Returns an array of available rooms and details of it:
    // isMainRoom (true,false), id, jid
    // participants: Participant[]
    //          id
    //          jid
    //          role
    //          displayName
    this.api.getRoomsInfo().then((rooms: any) => {
      // see response example structure
    });

    // Returns the current video quality setting.
    this.api.getVideoQuality();

    // Resolves to true if the device change is available and to false if not.
    // The accepted deviceType values are - 'output', 'input' or undefined.
    this.api.isDeviceChangeAvailable("output").then((isDeviceChangeAvailable: any) => {

    });

    // Resolves to true if the device list is available and to false if not.
    this.api.isDeviceListAvailable().then((isDeviceListAvailable: any) => {

    });

    // Resolves to true if multiple audio input is supported and to false if not.
    this.api.isMultipleAudioInputSupported().then((isMultipleAudioInputSupported: any) => {

    });

    // Selects the participant ID to be the pinned participant in order to always receive video for this participant.
    // The second parameter is optional and can be used to specify a videoType.
    // When multistream support is enabled by passing this parameter you can specify whether the desktop or the camera video for the specified participant should be pinned.
    // The accepted values are 'camera' and 'desktop'.
    // The default is 'camera'.
    // Any invalid values will be ignored and default will be used.
    this.api.pinParticipant("participantId", "videoType");

    // Resizes the large video container per the provided dimensions.
    this.api.resizeLargeVideo("width", "height");

    // Sets the audio input device to the one with the passed label or ID.
    this.api.setAudioInputDevice("deviceLabel", "deviceId");

    // Sets the audio output device to the one with the passed label or ID.
    this.api.setAudioOutputDevice("deviceLabel", "deviceId");

    // Displays the participant with the given participant ID on the large video.
    // If no participant ID is given, a participant is picked based on the dominant, pinned speaker settings.
    this.api.setLargeVideoParticipant("participantId");

    // Sets the video input device to the one with the passed label or ID.
    this.api.setVideoInputDevice("deviceLabel", "deviceId");

    // Starts a file recording or streaming session. See the startRecording command for more details.
    this.api.startRecording("options");

    // Stops an ongoing file recording or streaming session. See the stopRecording command for more details.
    this.api.stopRecording("mode");

    // Returns the number of conference participants:
    const numberOfParticipants = this.api.getNumberOfParticipants();

    // Returns a participant's display name:
    const displayName = this.api.getDisplayName("participantId");

    // Returns a participant's email:
    const email = this.api.getEmail("participantId");

    // Returns the IFrame HTML element which is used to load the Jitsi Meet conference:
    const iframe = this.api.getIFrame();

    // Returns a Promise which resolves to the current audio disabled state:
    this.api.isAudioDisabled().then((disabled: any) => {

    });

    // Returns a Promise which resolves to the current audio muted state:
    this.api.isAudioMuted().then((muted: any) => {

    });

    // Returns a Promise which resolves to the current video muted state:
    this.api.isVideoMuted().then((muted: any) => {

    });

    // Returns a Promise which resolves to the current audio availability state:
    this.api.isAudioAvailable().then((available: any) => {

    });

    // Returns a Promise which resolves to the current video availability state:
    this.api.isVideoAvailable().then((available: any) => {

    });

    // Returns a Promise which resolves to the current moderation state of the given media type.
    // mediaType can be either audio (default) or video.
    this.api.isModerationOn("mediaType").then((isModerationOn: any) => {

    });

    // Returns a Promise which resolves to a Boolean or null, when there is no conference.
    this.api.isP2pActive().then((isP2p: any) => {

    });

    // Returns a Promise which resolves to the current force mute state of the given participant for the given media type.
    // mediaType can be either audio (default) or video.
    // Force muted - moderation is on and participant is not allowed to unmute the given media type.
    this.api.isParticipantForceMuted("participantId", "mediaType").then((isForceMuted: any) => {

    });

    // Returns a Promise which resolves with the current participants pane state.
    this.api.isParticipantsPaneOpen().then((state: any) => {

    });

    // Returns a Promise which resolves with whether meeting was started in view only.
    this.api.isStartSilent().then((startSilent: any) => {

    });

    // Returns a Promise which resolves with the map of breakout rooms.
    // NOTE: The invitee format in the array depends on the invite service used in the deployment.
    // PSTN invite objects have the following structure:
    // {
    //     type: 'phone',
    //     number: <string> // the phone number in E.164 format  (ex. +31201234567)
    // }
    // SIP invite objects have the following structure:
    // {
    //     type: 'sip',
    //     address: <string> // the sip address
    // }
    this.api.listBreakoutRooms().then((breakoutRooms: any) => {

    });

    // Invite the given array of participants to the meeting:
    this.api.invite([{ "...": "" }, { "...": "" }, { "...": "" }]).then(() => {
      // success
    }).catch(() => {
      // failure
    });

    // Removes the embedded Jitsi Meet conference:
    this.api.dispose();
  }

  // Commands
  commands() {
    // Sets the display name of the local participant.
    // This command requires one argument to set the new display name.
    this.api.executeCommand('displayName', 'New Nickname');

    // Sets the password for the room.
    // set new password for channel
    this.api.addEventListener('participantRoleChanged', (event: any) => {
      if (event.role === "moderator") {
        this.api.executeCommand('password', 'The Password');
      }
    });

    // join a protected channel
    this.api.on('passwordRequired', () => {
      this.api.executeCommand('password', 'The Password');
    });

    // Toggles the lobby mode on or off.
    // This command requires the desired lobby mode state as the argument.
    this.api.addEventListener('participantRoleChanged', (event: any) => {
      if (event.role === 'moderator') {
        this.api.executeCommand('toggleLobby', true);
      }
    });

    // Touch tone playback.
    // This command requires the selected touch tone dial pads to play as well as the length of and time gap between tone play as the arguments.
    this.api.executeCommand('sendTones', {
      tones: "string", // The dial pad touch tones to play. For example, '12345#'.
      duration: "number", // Optional. The number of milliseconds each tone should play. The default is 200.
      pause: "number" // Optional. The number of milliseconds between each tone. The default is 200.
    });

    // Starts sharing a video
    // This command requires the an url pointing to either a youtube video or a video to be streamed from web (e.g an mp4 file)
    this.api.executeCommand('startShareVideo', "url");

    // Stops sharing a video (if the user is the one who started the video)
    // No arguments are required.
    this.api.executeCommand('stopShareVideo');

    // Sets the subject of the conference.
    // This command requires the new subject to be set as the argument and it will be applied only if the participant has the moderator role or after they receive that role later on.
    this.api.executeCommand('subject', 'New Conference Subject');

    // Sets the local subject of the conference.
    // This command requires the new local subject to be set as the argument and it can be applied by all participants regardless of their role.
    this.api.executeCommand('localSubject', 'New Conference Local Subject');

    // Mutes / unmutes the audio for the local participant.
    // No arguments are required.
    this.api.executeCommand('toggleAudio');

    // Mutes / unmutes the video for the local participant.
    // No arguments are required.
    this.api.executeCommand('toggleVideo');

    // Hide or show the filmstrip.
    // No arguments are required.
    this.api.executeCommand('toggleFilmStrip');

    // Hide or show chat messaging.
    // No arguments are required.
    this.api.executeCommand('toggleChat');

    // Hide or show the raised hand.
    // No arguments are required.
    this.api.executeCommand('toggleRaiseHand');

    // Start or stop screen sharing.
    // No arguments are required.
    this.api.executeCommand('toggleShareScreen');

    // Enable or disable noise suppression on the current audio track.
    this.api.executeCommand('setNoiseSuppressionEnabled', {
      enabled: false // Enable or disable noise suppression.
    });

    // Start or stop subtitles.
    // No arguments are required.
    this.api.executeCommand('toggleSubtitles');

    // Enter or exit the tile view layout mode.
    // No arguments are required.
    this.api.executeCommand('toggleTileView');

    // Ends the call.
    // No arguments are required.
    this.api.executeCommand('hangup');

    // Ends the current conference for everyone.
    // This command can only be executed by a meeting moderator, and requires End Conference support to be enabled for the deployment.
    this.api.executeCommand('endConference');

    // Changes the local email address.
    // This command requires the new email address as the single argument.
    this.api.executeCommand('email', 'example@example.com');

    // Changes the local avatar URL.
    // This command requires the new avatar URL to be set as the single argument.
    this.api.executeCommand('avatarUrl', 'https://avatars0.githubusercontent.com/u/3671647');

    // Sends a text message to another participant through the data channels.
    this.api.executeCommand('sendEndpointTextMessage', 'receiverParticipantId', 'text');

    // Displays the participant on the large video display.
    // The participant ID, if specified, is displayed on the large video.
    // If no argument is passed, the participant to be displayed on the large video is automatically selected based on the dominant/pinned speaker settings.
    // The second parameter is optional and can be used to specify a videoType.
    // When multistream support is enabled by passing this parameter you can specify whether the desktop or the camera video for the specified participant should be selected.
    // The accepted values are 'camera' and 'desktop'. The default is 'camera'. Any invalid values will be ignored and default will be used.
    this.api.executeCommand('setLargeVideoParticipant', 'abcd1234', 'desktop');

    // Sets the send and receive video resolution.
    // The resolution height setting is implemented using a single argument.
    this.api.executeCommand('setVideoQuality', 720);

    // Mute all meeting participants.
    // This command can only be executed by the meeting moderator and can take one argument: mediaType - for which media type to mute everyone.
    // mediaType can be either 'audio' (default) or 'video'.
    this.api.executeCommand('muteEveryone', 'video');

    // Starts a local recording, file recording or streaming session using passed parameters:
    // RTMP streaming - Recording mode set to stream with an rtmpStreamKey. The rtmpBroadcastID value is optional.
    // YouTube streams - Recording mode set to stream with an youtubeStreamKey. The youtubeBroadcastID value is optional.
    // Local Recording - Recording mode set to local. The onlySelf value is optional.
    // Dropbox recording - Recording mode set to file with a Dropbox OAuth2 token.
    // Additionally, Dropbox saving should be enabled on the Jitsi meet deploy config you are using.
    // File recording - Recording mode set to file.
    // Optionally, shouldShare should be passed on. No other params are required.
    this.api.executeCommand('startRecording', {
      mode: "string", //recording mode, either `local`, `file` or `stream`.
      dropboxToken: "string", //dropbox oauth2 token.
      onlySelf: false,  //Whether to only record the local streams. Only applies to `local` recording mode.
      shouldShare: "boolean", //whether the recording should be shared with the participants or not. Only applies to certain jitsi meet deploys.
      rtmpStreamKey: "string", //the RTMP stream key.
      rtmpBroadcastID: "string", //the RTMP broadcast ID.
      youtubeStreamKey: "string", //the youtube stream key.
      youtubeBroadcastID: "string" //the youtube broacast ID.
    });

    // Stops an ongoing local, stream or file recording.
    // The mode in which the recording was started must be specified.
    // recording mode to stop, `local`, `stream` or `file`
    this.api.executeCommand('stopRecording', "mode");

    // Opens the chat window and sets the participant with the given participant ID as the messages recipient.
    this.api.executeCommand('initiatePrivateChat', "participantID");

    // Removes the private chat participant thus it resets the chat window to group chat.
    this.api.executeCommand('cancelPrivateChat');

    // Kicks the participant with the given participant ID from the meeting.
    this.api.executeCommand('kickParticipant', "participantID");

    // Grants moderator rights to the participant with the given ID.
    this.api.executeCommand('grantModerator', "participantID");

    // Overwrite config.js props with values from the config object passed on to the command.
    let config: Object = {
      toolbarButtons: ['chat'] // will overwrite the toolbarButtons config value with [chat], resulting in UI only showing the chat button.
    }

    this.api.executeCommand('overwriteConfig', config);

    // Sends a chat message either to a specific participant or as a group chat message.
    this.api.executeCommand('sendChatMessage',
      "message", //the text message
      "to", // the receiving participant ID or empty string/undefined for group chat.
      false // ignorePrivacy - true if the privacy notification should be ignored. Defaulted to false.
    );

    // Allows moderators to toggle the follow me functionality
    // set to true if participants should be following you, false otherwise
    this.api.executeCommand('setFollowMe', false);

    // Enables or disables the subtitles.
    this.api.executeCommand('setSubtitles', true);

    // Enables or disables the tileview mode.
    this.api.executeCommand('setTileView', true);

    // Approves or rejects the knocking participant in the lobby.
    this.api.executeCommand('answerKnockingParticipant',
      "id", // the participant id
      true // approved
    );

    // Toggles the front/back camera on mobile web.
    this.api.executeCommand('toggleCamera');

    // Toggles the mirroring of the local video.
    this.api.executeCommand('toggleCameraMirror');

    // Toggles the virtual background selection dialog.
    this.api.executeCommand('toggleVirtualBackgroundDialog');

    // Pins a conference participant.
    // The ID of the conference participant to pin or null to unpin all
    this.api.executeCommand('pinParticipant', "id");

    // Change volume of the participant with the given participant ID.
    // volume - number between 0 and 1
    this.api.executeCommand('setParticipantVolume', "participantID", 100);

    // Changes the visibility status of the participants pane.
    // enabled - The visibility status of the participants pane.
    this.api.executeCommand('toggleParticipantsPane', true);

    // Changes moderation status of the given media type.
    // This command requires two arguments: enable - whether to enable it or not, and mediaType - the media type for which to change moderation.
    // mediaType can be 'audio' (default) or 'video'
    this.api.executeCommand('toggleModeration',
      true, // enable
      "mediaType" // can be 'audio' (default) or 'video'
    );

    // Asks the participant with the given ID to unmute. If audio moderation is on it also approves the participant for audio.
    this.api.executeCommand('askToUnmute', "participantId");

    // If video moderation is on it approves the participant with the given ID for video.
    this.api.executeCommand('approveVideo', "participantId");

    // Rejects the participant with the given ID from moderation of the given media type.
    this.api.executeCommand('rejectParticipant',
      "participantId",
      "mediaType" // can be 'audio' (default) or 'video'
    );

    // Creates a breakout room.
    // This command can only be executed by the meeting moderator.
    this.api.executeCommand('addBreakoutRoom',
      "name" // Optional. The name or subject of the new room.
    );

    // Auto-assigns the participants to breakout rooms.
    // This command can only be executed by the meeting moderator.
    this.api.executeCommand('autoAssignToBreakoutRooms');

    // Closes the breakout room and sends participants to the main room.
    // This command can only be executed by the meeting moderator.
    this.api.executeCommand('closeBreakoutRoom',
      "roomId" // The id of the room to close.
    );

    // Joins a breakout room. If the argument is omitted, joins the main room.
    this.api.executeCommand('joinBreakoutRoom',
      "roomId" // Optional. The id of the room to join.
    );

    // Removes the breakout room.
    // This command can only be executed by the meeting moderator.
    this.api.executeCommand('removeBreakoutRoom',
      "breakoutRoomJid" // The jid of the breakout room to remove.
    );

    // Resizes the filmstrip.
    this.api.executeCommand('resizeFilmStrip', {
      width: 720 // The desired filmstrip width
    });

    // Resizes the large video container based on the dimensions provided.
    this.api.executeCommand('resizeLargeVideo',
      720, // The desired width
      480 // The desired height
    );

    // Sends a participant to a room.
    // This command can only be executed by the meeting moderator.
    this.api.executeCommand('sendParticipantToRoom',
      "participantId", // The id of the participant.
      "roomId" // The id of the room.
    );

    // Overwrites the names of the given participants to the given names. (locally for the participant that send the command)
    this.api.executeCommand('overwriteNames', [{
      id: "String", // The id of the participant.
      name: "String" // The new name.
    }]);

    // Shows a custom notification. This affects only the local user.
    // If uid is provided, the notification will replace existing notification with the same uid. The uid can also be passed to the hideNotification command to programmatically hide the notification.
    this.api.executeCommand('showNotification', {
      title: "String", // Title of the notification.
      description: "String", // Content of the notification.
      uid: "String", // Optional. Unique identifier for the notification.
      type: "String", // Optional. Can be 'info', 'normal', 'success', 'warning' or 'error'. Defaults to 'normal'.
      timeout: "String" // optional. Can be 'short', 'medium', 'long', or 'sticky'. Defaults to 'short'.
    });

    // Hides the notification which has the given uid.
    // Unique identifier for the notification to be removed.
    this.api.executeCommand('hideNotification', "uid");

    // Toggles the whiteboard to open, repeated toggling hidden the whiteboard
    this.api.executeCommand('toggleWhiteboard');
  }

  // Events
  // The JitsiMeetExternalAPI object implements the EventEmitter API for emitting and listening for events.
  // You can add event listeners to the embedded Jitsi Meet using the addListener method:
  //    api.addListener(event, listener);
  // If you want to remove a listener you can use the removeListener method:
  //    api.removeListener(event, listener);
  // The event parameter is a string object with the name of the event.
  // The listener parameter is a function object with one argument that creates a notification when the event occurs along with related event data.

  // Provides event notifications about Jitsi Meet having failed to access the meeting camera.
  // The listener receives an object with the following structure:
  // {
  //     type: string, // A constant representing the overall type of the error.
  //     message: string // Additional information about the error.
  // }
  cameraErrorEvent = async (event: any) => {

  }

  //Provides event notifications about changes to a participant's avatar.
  // The listener receives an object with the following structure:
  // {
  //     id: string, // the id of the participant that changed his avatar.
  //     avatarURL: string // the new avatar URL.
  // }
  avatarChangedEvent = async (event: any) => {

  }

  // Provides event notifications about changes to audio availability status.
  // The listener receives an object with the following structure:
  // {
  //     available: boolean // new available status - boolean
  // }
  audioAvailabilityChangedEvent = async (event: any) => {

  }

  // Provides event notifications about changes to audio mute status.
  // The listener receives an object with the following structure:
  // {
  //     muted: boolean // new muted status - boolean
  // }
  audioMuteStatusChangedEvent = (audio: any) => {
    console.log('handleMuteStatus', audio); // { muted: true }
  };

  // Provides notifications about breakout rooms changes.
  // The listener receives an object with the following structure:
  // {
  //     [roomId]: {
  //         id: string,
  //         jid: string,
  //         name: string,
  //         isMainRoom: true | undefined,
  //         participants: {
  //             [participantJid]: {
  //                 displayName: string,
  //                 jid: string,
  //                 role: string
  //             }
  //         }
  //     },
  //     ...
  // }
  breakoutRoomsUpdatedEvent = async (event: any) => {

  }

  // Provides event notifications about the current browser support.
  // The listener receives an object with the following structure:
  // {
  //     supported: boolean
  // }
  browserSupportEvent = async (event: any) => {

  }

  // Provides real-time list of currently screen sharing participant ID's.
  // The listener receives an object with the following structure:
  // {
  //     data: ["particId1", "particId2", ...]
  // }
  contentSharingParticipantsChangedEvent = async (event: any) => {

  }

  // Indicates the data channel is open and thus messages can be sent over it.
  dataChannelOpenedEvent = async (event: any) => {

  }

  // Provides event notifications about a text messages received through data channels.
  // The listener receives an object with the following structure:
  // {
  //     senderInfo: {
  //         jid: string, // the jid of the sender
  //         id: string // the participant id of the sender
  //     },
  //     eventData: {
  //         name: string, // the name of the datachannel event: `endpoint-text-message`
  //         text: string // the received text from the sender
  //     }
  // }
  endpointTextMessageReceivedEvent = async (event: any) => {
    console.log('mensagem', event, event.data.eventData.text);

    if ((event.data.eventData.text = 'mover a principal')) {
      //this.startRoom('grupo 1', true);
    }
  };

  // Provides event notifications about a messages sent by a non-participant, e.g. a custom prosody message.
  // The listener receives an object with the following structure:
  // {
  //         id: string, // the id of the message, may be null
  //         message: string // the message received
  // }
  nonParticipantMessageReceivedEvent = async (event: any) => {

  }

  // Provides event notifications when a face landmark is detected
  // The listener receives an object with the following structure:
  // {
  //     faceBox: {
  //         left: number, // face bounding box distance as percentage from the left video edge
  //         right: number // face bounding box distance as percentage from the right video edge
  //         width: number // face bounding box width as percentage of the total video width
  //     }, // this might be undefined if config.faceLandmarks.faceCenteringThreshold is not passed
  //     faceExpression: string // check https://github.com/jitsi/jitsi-meet/blob/master/react/features/face-landmarks/constants.js#L3 for available values
  // }
  faceLandmarkDetectedEvent = async (event: any) => {

  }

  // Provides event notifications about an error which has occurred.
  // The listener receives an object with the following structure:
  // {
  //     details: Object?, // additional error details
  //     message: string?, // the error message
  //     name: string, // the coded name of the error
  //     type: string, // error type/source, one of : 'CONFIG', 'CONNECTION', 'CONFERENCE'
  //     isFatal: boolean // whether this is a fatal error which triggered a reconnect overlay or not
  // }
  errorOccurredEvent = async (event: any) => {

  }

  // Provides event notifications about a knocking participant in the lobby.
  // The listener receives an object with the following structure:
  // {
  //     participant: {
  //         // the id and name of the participant that is currently knocking in the lobby
  //         id: string,
  //         name: string
  //     }
  // }
  knockingParticipantEvent = async (event: any) => {

  }

  // Provides event notifications about changes in the large video display.
  // The listener receives an object with the following structure:
  // {
  //     id: string // id of the participant that is now on large video in the stage view.
  // }
  largeVideoChangedEvent = async (event: any) => {

  }

  // Provides log event notifications with the log level being one of the values specified in the config.js file in the apiLogLevels property (if not specified the event does not fire).
  // The listener receives an object with the following structure:
  // {
  //     logLevel: string, // A constant representing the log type (info, error, debug, warn).
  //     args: string // Additional log information.
  // }
  logEvent = async (event: any) => {

  }


  // ########################
  getParticipants() {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        resolve(this.api.getParticipantsInfo()); // get all participants
      }, 500);
    });
  }

  // custom events
  executeCommand(command: string) {
    this.api.executeCommand(command); //

    if (command == 'hangup') {
      this.router.navigate(['/thank-you']);
      return;
    }

    if (command == 'toggleAudio') {
      this.isAudioMuted = !this.isAudioMuted;
    }

    if (command == 'toggleVideo') {
      this.isVideoMuted = !this.isVideoMuted;
    }
  }

  readyToCloseEvent = () => {
    console.log('handleClose');
  };

  participantLeftEvent = async (participant: any) => {
    console.log('handleParticipantLeft', participant); // { id: "2baa184e" }
    const data = await this.getParticipants();
  };

  participantJoinedEvent = async (participant: any) => {
    console.log('OJOJOJOJ  handleParticipantJoined', participant); // { id: "2baa184e", displayName: "Shanu Verma", formattedDisplayName: "Shanu Verma" }

    const data = await this.getParticipants();
  };

  videoConferenceJoinedEvent = async (participant: any) => {
    console.log('handleVideoConferenceJoined', participant); // { roomName: "bwb-bfqi-vmh", id: "8c35a951", displayName: "Akash Verma", formattedDisplayName: "Akash Verma (me)"}

    this.user.setName(participant.userNameTest);
    this.roomName = participant.roomName;

    const data = await this.getParticipants();
  };

  videoConferenceLeftEvent = () => {
    console.log('handleVideoConferenceLeft');
    this.router.navigate(['/thank-you']);
  };

  videoMuteStatusChangedEvent = (video: any) => {
    console.log('handleVideoStatus', video); // { muted: true }
  };

  participantRoleChangedEvent = async (participant: any) => {
    console.log('participantRoleChanged', participant);

    if (participant.role === "moderator") {
      console.log('participantRoleChanged:', participant.role);
      this.api.executeCommand('password', 'The Password');
    }
  };

  passwordRequiredEvent = async () => {
    console.log('passwordRequired'); // { id: "2baa184e" }
    this.api.executeCommand('password', 'The Password');
  };


  // Utils

  makeRandom(lengthOfCode: number, possible: string): string {
    let text = "";
    for (let i = 0; i < lengthOfCode; i++) {
      text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
  }

  changeRouterLink(value: any) {
    console.log(value);
    this.roomName = value;

    let myNode: HTMLElement = <HTMLElement>document.getElementById('jitsi-iframe');
    myNode.innerHTML = '';

    this.api = new JitsiMeetExternalAPI(this.domain, this.options);
  }
}
