import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Article } from 'src/app/models/article.model';
import { ArticleService } from 'src/app/services/article.service';

@Component({
  selector: 'app-article-detail',
  templateUrl: './article-detail.component.html',
  styleUrls: ['./article-detail.component.scss']
})
export class ArticleDetailComponent implements OnInit {
  public id: string = '';
  public article: Article = new Article();

  constructor(
    private articleService: ArticleService,
    private activeRoute: ActivatedRoute,
    private router: Router) {
  }

  ngOnInit(): void {
    this.id = this.activeRoute.snapshot.params['id'];

    if(this.id){
      this.GetArticle();
    }
  }

  public async GetArticle() {
    this.articleService.GetById(this.id).then(async (response: Article) => {
      if (response) {
        this.article = response;
      }
    }).catch((err) => { });
  }
}
<div class="container conteudo">
  <div class="row conteudo__artigo">
    <div class="col-6">
      <img class="conteudo__artigo__img" src="{{article.ImageUrl}}" alt="{{article.Title}}">
    </div>
    <div class="col-6">
      <section class="conteudo__artigo__texto">
        <h2 class="conteudo__artigo__texto__titulo">{{article.Title}}</h2>
        <p class="conteudo__artigo__texto__paragrafo">{{article.Headlight}}</p>
      </section>
    </div>
  </div>
  <div class="row conteudo__artigo">
    <div class="col-12">
      <section class="">
        <p class="conteudo__artigo__texto__paragrafo">{{article.Content}}</p>
      </section>
    </div>
  </div>
</div>

<div class="barra_pesquisa">
  <button class="botao" [routerLink]="['/noticias']">Voltar</button>
</div>
