import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { Article } from 'src/app/models/article.model';
import { ArticleService } from 'src/app/services/article.service';

@Component({
  selector: 'app-article',
  templateUrl: './article.component.html',
  styleUrls: ['./article.component.scss']
})
export class ArticleComponent implements OnInit {

  public articles: Article[] = [];

  constructor(
    private articleService: ArticleService,
    private router: Router) {
  }

  ngOnInit(): void {
    this.articleService.GetAll().then(async (response: Article[]) => {
      if (response && response.length > 0) {
        this.articles = response;
      }
    }).catch((err) => {

    });
  }
}
<div class="barra_pesquisa">
  <h2 class="barra_pesquisa__titulo">O que você quer saber?</h2>
  <input class="barra_pesquisa__pesquisa" type="text" placeholder="Faça sua busca aqui">
  <button class="botao">Buscar</button>
</div>

<div class="container conteudo">
  <div class="row conteudo__artigo" *ngFor="let item of articles">
    <div class="col-6">
      <img class="conteudo__artigo__img" src="{{item.ImageUrl}}" alt="{{item.Title}}">
    </div>
    <div class="col-6">
      <section class="conteudo__artigo__texto">
        <h2 class="conteudo__artigo__texto__titulo">{{item.Title}}</h2>
        <p class="conteudo__artigo__texto__paragrafo">{{item.Content | slice:0:300}}</p>
        <a class="botao" [routerLink]="['/noticias/', item.Id]">Saiba mais</a>
      </section>
    </div>
  </div>
</div>
