import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.scss']
})
export class HomeComponent implements OnInit {
  constructor(
    private router: Router
  ) { }

  async ngOnInit() {

  }
}
<div class="carousel-container">
  <div class="carousel-inner">
    <img src="./assets/img/gallery1.png" alt="Image 1" class="carousel-image active">
    <img src="./assets/img/gallery2.png" alt="Image 2" class="carousel-image">
    <img src="./assets/img/gallery3.png" alt="Image 3" class="carousel-image">
    <img src="./assets/img/gallery4.png" alt="Image 4" class="carousel-image">
    <img src="./assets/img/gallery5.png" alt="Image 5" class="carousel-image">
  </div>
  <button class="prev-button">Previous</button>
  <button class="next-button">Next</button>
</div>

<script>
  const carouselInner = document.querySelector('.carousel-inner');
  const prevButton = document.querySelector('.prev-button');
  const nextButton = document.querySelector('.next-button');

  let activeImageIndex = 0;

  function updateCarouselImage() {
    carouselInner.style.transform = `translateX(-${activeImageIndex * 100}%`;
  }

  updateCarouselImage();

  prevButton.addEventListener('click', () => {
    activeImageIndex = activeImageIndex === 0 ? 4 : activeImageIndex - 1;
    updateCarouselImage();
  });

  nextButton.addEventListener('click', () => {
    activeImageIndex = activeImageIndex === 4 ? 0 : activeImageIndex + 1;
    updateCarouselImage();
  });
</script>
